import axios from "axios";

const api = axios.create({
    baseURL:"https://api.coinpaprika.com/v1"
})

api.get('/exchanges');

const coinApi = {
    showTickers :() => api.get('/tickers'),
    showExchange : () => api.get('/exchanges'),
    showCoins:() => api.get('/coins')

}
