import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";

const Container = styled.div``;
const List = styled.ul`
display:grid
`;
const Item = styled.li``

// {showPricesList && showPricesList.length > 0 && <div>{showPricesList.map(price => price.id)}</div>}

const PricesPresenter = ({showPricesList,error,loading}) => loading? null:
<Container>
    <List>
          {showPricesList && showPricesList.length > 0 && <Item>{showPricesList.map(price => price.id)}</Item>}       
    </List>
</Container> 

PricesPresenter.prototype={
    showPricesList:PropTypes.array,
    error:PropTypes.string,
    loading:PropTypes.bool.isRequired
}

export default PricesPresenter;
