import React from "react";
import styled from "styled-components";
import PropTypes from "prop-types";

const Container = styled.div`
display:grid`;
const List = styled.div`

`;
const Item = styled.div``


const PricesPresenter = ({showPricesList,error,loading}) => loading? (<Container>loading....</Container>):

<Container>
{showPricesList && showPricesList.length > 0 && <div>{showPricesList.map(price =>{
    return <div id={price.id}>{price.name} / {price.symbol} : ${price.quotes.USD.price} </div>
})}</div>}
</Container> 

PricesPresenter.prototype={
    showPricesList:PropTypes.array,
    error:PropTypes.string,
    loading:PropTypes.bool.isRequired
}

export default PricesPresenter;
